from typing import Union


def make_addition(first_element: Union[int, float], second_element: Union[int, float]) -> Union[int, float]:
    """Addition for calculator

    Args:
        first_element (Union[int, float]): first element of addition
        second_element (Union[int, float]): second element of addition

    Returns:
        Union[int, float]: result of addition
    """
    return first_element + second_element
