from .imports import *

class ClassificationType(Enum):
    """Enum Class for Classification Type."""
    BINARY = "Binary"
    MULTICLASS = "Multiclass"

class RegressionType(Enum):
    """Enum Class for Regression Type."""
    LINEAR = "Linear"
    NONLINEAR = "Nonlinear"

class Config:
    """Getter Class for Configurations for XAI. (HARD CODED)"""

    def __init__(self) -> None:
        pass

    def get_missing_value_threshold(self):
        """Returns the threshold for missing value percentage in a feature."""
        return 60

    def get_supervised_type_threshold(self):
        """Returns the threshold for determining classification task based on nunique of target feature."""
        return 20 + 1

    def get_categorical_feature_threshold(self):
        """Returns the threshold for determining categorical feature based on nunique of feature."""
        return 50 + 1

    def high_cardinality_features_threshold(self):
        """Returns the threshold for determining high cardinality feature based on nunique of feature."""
        return 50 + 1
    
    def get_shap_sample_percentage(self):
        """Returns the sample size for SHAP."""
        return 0.4

    def get_classification_type(self, binary=True):
        """Returns Supervised Task"""
        if(binary):
            return ClassificationType.BINARY
        else:
            return ClassificationType.MULTICLASS

    def get_regression_type(self, linear=True):
        """Returns Supervised Task"""
        if(linear):
            return RegressionType.LINEAR
        else:
            return RegressionType.NONLINEAR


